          <div className="flex justify-center items-center gap-1 sm:gap-2">
            {orderedKeys.map((key, index) => {
              const pillarColor = (orderedPillars[key as keyof typeof orderedPillars]?.color as string) || 'verto-green';
              const accentColor = getAccentColor(pillarColor);

              return (
                <div key={index} className="flex items-center">
                  <button
                    onClick={() => handleStepClick(index)}
                    className={`relative flex items-center justify-center transition-all duration-300 cursor-pointer group ${
                      index === currentStep && !isAutoPlaying ? 'animate-bubble-pop scale-110' : 'hover:opacity-80'
                    }`}
                  >
                    <div
                      className={`w-10 h-10 sm:w-12 sm:h-12 rounded-full flex items-center justify-center font-bold text-sm sm:text-base transition-all duration-300 ${
                        index === currentStep ? 'shadow-lg' : 'shadow-md'
                      }`}
                      style={{
                        backgroundColor:
                          index <= currentStep
                            ? accentColor
                            : `${accentColor}40`,
                        color: index <= currentStep ? 'white' : accentColor,
                        boxShadow:
                          index === currentStep
                            ? `0 0 20px ${accentColor}80`
                            : index < currentStep
                            ? `0 0 10px ${accentColor}40`
                            : 'none',
                      }}
                    >
                      {index === orderedKeys.length - 1 ? (
                        <TrendingUp className="w-5 h-5 sm:w-6 sm:h-6" />
                      ) : (
                        index + 1
                      )}
                    </div>
                  </button>
                  {index < orderedKeys.length - 1 && (
                    <div
                      key={`dash-${index}-${currentStep}`}
                      className={`hidden sm:block w-8 md:w-12 h-0.5 mx-1 md:mx-2 rounded-full overflow-hidden relative`}
                      style={{
                        background: index < currentStep 
                          ? `linear-gradient(90deg, ${accentColor}, ${getAccentColor((orderedPillars[orderedKeys[index + 1] as keyof typeof orderedPillars]?.color as string) || 'verto-green')})`
                          : 'rgba(148, 163, 184, 0.3)',
                      }}
                    >
                      <div
                        className="h-full"
                        style={{
                          width: index < currentStep ? '100%' : index === currentStep ? '0%' : '0%',
                          background: `linear-gradient(90deg, ${accentColor}, ${getAccentColor((orderedPillars[orderedKeys[index + 1] as keyof typeof orderedPillars]?.color as string) || 'verto-green')})`,
                          transition: index === currentStep ? 'width 10000ms linear' : 'none',
                          animation: index === currentStep ? 'fillDash 10000ms linear forwards' : 'none',
                        }}
                      />
                    </div>
                  )}
                </div>
              );
            })}
          </div>
        </div>
      </div>

      {/* DESIGN CHANGE: The main content container now has more consistent padding */}
      <div id="pillar-content" className="max-w-6xl mx-auto px-4 sm:px-6 lg:px-8 py-8 md:py-12">
        <div className="grid lg:grid-cols-2 gap-12 lg:gap-24 lg:items-center">
          {/* Left Column: Text Content */}
          <div className="lg:col-span-1">
            <div>
              <p className={`text-sm font-semibold uppercase tracking-wider ${activeColors.text}`}>{activePillar?.label || 'Unknown'}</p>
              <h3 className="text-3xl md:text-4xl font-bold text-slate-900 dark:text-white mt-2 mb-4">{activePillar?.title || 'Unknown Title'}</h3>
              <p className="text-lg text-slate-600 dark:text-slate-300 leading-relaxed">{activePillar?.description || 'No description available'}</p>
            </div>

            {/* Mobile Animation - Renders below description on small screens */}
            <div className="lg:hidden my-10">
              {activePillar?.visual}
            </div>

            <div className="space-y-6 md:space-y-8 mt-10 mb-10">
              {activePillar?.features?.map((feature: any) => (
                <FeatureItem key={feature.title} icon={feature.icon} title={feature.title}>{feature.description}</FeatureItem>
              )) || []}
            </div>
            <button
              onClick={openModal}
              className={`inline-flex items-center px-6 py-3 ${activeColors.bg} hover:bg-opacity-90 text-white font-semibold rounded-lg transition-colors`}
            >
              <span>{activePillar?.cta || 'Get Started'}</span>
              <ArrowRight className="w-4 h-4 ml-2" />
            </button>
          </div>

          {/* Right Column: Visual */}
          <div className="hidden lg:flex lg:col-span-1 justify-center">
            <div className="w-full max-w-md">
              {activePillar?.visual}
            </div>
          </div>
        </div>

        {/* DESIGN CHANGE: Founder Insight Banner moved to bottom, after benefits and UX slideshow */}
        {activePillar?.founderQuote && <FounderInsightBanner {...activePillar.founderQuote} colorClasses={activeColors} />}
      </div>

      <CalendlyModal title={`Get Started with ${activePillar?.label || 'Service'}`} />
    </div>
  );
}

