import express, { type Request, Response, NextFunction } from "express";
import { registerRoutes } from "./routes";
import { setupVite, serveStatic, log } from "./vite";

const app = express();
app.use(express.json());
app.use(express.urlencoded({ extended: false }));

app.use((req, res, next) => {
  const start = Date.now();
  const path = req.path;
  let capturedJsonResponse: Record<string, any> | undefined = undefined;

  const originalResJson = res.json;
  res.json = function (bodyJson, ...args) {
    capturedJsonResponse = bodyJson;
    return originalResJson.apply(res, [bodyJson, ...args]);
  };

  res.on("finish", () => {
    const duration = Date.now() - start;
    if (path.startsWith("/api")) {
      let logLine = `${req.method} ${path} ${res.statusCode} in ${duration}ms`;
      if (capturedJsonResponse) {
        logLine += ` :: ${JSON.stringify(capturedJsonResponse)}`;
      }

      if (logLine.length > 80) {
        logLine = logLine.slice(0, 79) + "…";
      }

      log(logLine);
    }
  });

  next();
});

(async () => {
  const server = await registerRoutes(app);

  app.use((err: any, _req: Request, res: Response, _next: NextFunction) => {
    const status = err.status || err.statusCode || 500;
    const message = err.message || "Internal Server Error";

    res.status(status).json({ message });
    throw err;
  });

  // importantly only setup vite in development and after
  // setting up all the other routes so the catch-all route
  // doesn't interfere with the other routes
  if (app.get("env") === "development") {
    await setupVite(app, server);
  } else {
    serveStatic(app);
  }

  // ALWAYS serve the app on the port specified in the environment variable PORT
  // Other ports are firewalled. Default to 5000 if not specified.
  // this serves both the API and the client.
  // It is the only port that is not firewalled.
  const port = parseInt(process.env.PORT || '5001', 10);
  server.listen({
    port,
    host: "0.0.0.0",
  }, () => {
    log(`serving on port ${port}`);
  });
})();

client/src/index.css.content:
@import url('https://fonts.googleapis.com/css2?family=General+Sans:ital,wght@0,200;0,300;0,400;0,500;0,600;0,700;0,800;1,200;1,300;1,400;1,500;1,600;1,700;1,800&display=swap');
@tailwind base;
@tailwind components;
@tailwind utilities;

:root {
  --background: hsl(0, 0%, 0%);
  --foreground: hsl(200, 6.6667%, 91.1765%);
  --card: hsl(228, 9.8039%, 10%);
  --card-foreground: hsl(0, 0%, 85.0980%);
  --popover: hsl(0, 0%, 0%);
  --popover-foreground: hsl(200, 6.6667%, 91.1765%);
  --primary: hsl(203.7736, 87.6033%, 52.5490%);
  --primary-foreground: hsl(0, 0%, 100%);
  --secondary: hsl(195.0000, 15.3846%, 94.9020%);
  --secondary-foreground: hsl(210, 25%, 7.8431%);
  --muted: hsl(0, 0%, 9.4118%);
  --muted-foreground: hsl(210, 3.3898%, 46.2745%);
  --accent: hsl(205.7143, 70%, 7.8431%);
  --accent-foreground: hsl(203.7736, 87.6033%, 52.5490%);
  --destructive: hsl(356.3033, 90.5579%, 54.3137%);
  --destructive-foreground: hsl(0, 0%, 100%);
  --border: hsl(210, 5.2632%, 14.9020%);
  --input: hsl(207.6923, 27.6596%, 18.4314%);
  --ring: hsl(202.8169, 89.1213%, 53.1373%);
  --radius: 1.3rem;

  /* Verto Brand Colors - Official Brand Palette */
  --verto-gradient-start: hsl(34, 86%, 61%); /* EEAA4A */
  --verto-gradient-pink: hsl(20, 95%, 49%); /* EF660B */
  --verto-gradient-middle: hsl(257, 100%, 72%); /* 8F73FE */
  --verto-gradient-end: hsl(217, 100%, 56%); /* 217DFE */
  --verto-purple: hsl(257, 100%, 72%);
  --verto-blue: hsl(217, 100%, 56%);
  --verto-green: hsl(127, 80%, 45%);
  --verto-orange: hsl(34, 86%, 61%);
  --verto-pink: hsl(20, 95%, 49%);
  --verto-cyan: hsl(185, 80%, 55%);

  /* Verto Gray Scale */
  --verto-gray-50: hsl(210, 20%, 98%);
  --verto-gray-100: hsl(220, 14%, 93%);
  --verto-gray-200: hsl(220, 13%, 91%);
  --verto-gray-400: hsl(218, 11%, 65%);
  --verto-gray-500: hsl(220, 9%, 46%);
  --verto-gray-600: hsl(215, 14%, 34%);
  --verto-gray-900: hsl(220, 39%, 11%);

  /* Font Family */
  --font-sans: 'General Sans', system-ui, -apple-system, sans-serif;
}

@layer base {
  * {
    @apply border-border;
  }

  body {
    @apply font-sans antialiased bg-background text-foreground;
    font-family: var(--font-sans);
  }

  html {
    scroll-behavior: smooth;
  }
}

@layer utilities {
  .animate-fade-in {
    animation: fadeIn 0.6s ease-out;
  }

  .animate-slide-up {
    animation: slideUp 0.6s ease-out;
  }

  .verto-gradient {
    background: linear-gradient(135deg, var(--verto-gradient-start), var(--verto-gradient-middle), var(--verto-gradient-end));
  }

  .verto-gradient-text {
    background: linear-gradient(135deg, var(--verto-gradient-start), var(--verto-gradient-middle), var(--verto-gradient-end));
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    background-clip: text;
  }

  .glass-card {
    background: rgba(255, 255, 255, 0.1);
    backdrop-filter: blur(10px);
    border: 1px solid rgba(255, 255, 255, 0.2);
  }

  @keyframes fadeIn {
    from {
      opacity: 0;
    }
    to {
      opacity: 1;
    }
  }

  @keyframes slideUp {
    from {
      opacity: 0;
      transform: translateY(20px);
    }
    to {
      opacity: 1;
      transform: translateY(0);
    }
  }

  @keyframes depthShift {
    0%, 100% {
      box-shadow: 0 10px 30px rgba(0, 0, 0, 0.1);
      transform: translateY(0);
    }
    50% {
      box-shadow: 0 15px 40px rgba(0, 0, 0, 0.15);
      transform: translateY(-2px);
    }
  }

  .animate-gradient {
    animation: depthShift 5s ease-in-out infinite;
  }

  @keyframes navGlow {
    0%, 100% {
      box-shadow: 0 4px 24px rgba(0, 0, 0, 0.12), inset 0 1px 0 rgba(255, 255, 255, 0.15);
    }
    50% {
      box-shadow: 0 6px 32px rgba(0, 0, 0, 0.18), inset 0 1px 0 rgba(255, 255, 255, 0.2);
    }
  }

  .animate-nav-glow {
    animation: navGlow 6s ease-in-out infinite;
  }

  /* Dawn Gradient - dynamic radial that changes with scroll */
  .dawn-gradient {
    position: relative;
    --dawn-color-1: #070911;
    --dawn-color-2: #070911;
    --dawn-color-3: #0A0D15;
    --dawn-color-4: #0F1218;
    --dawn-color-5: #15151C;
    --dawn-color-6: #1A1818;
    --dawn-color-7: #261E14;
    --dawn-color-8: #3A280E;
  }

  .dawn-gradient::before {
    content: '';
    position: fixed;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    z-index: -1;
    pointer-events: none;
    /* Radial gradient: dark dominant color at center, spreading outward subtly */
    background: radial-gradient(
      ellipse 120% 120% at 50% 50%,
      var(--dawn-color-1) 0%,
      var(--dawn-color-2) 12%,
      var(--dawn-color-3) 24%,
      var(--dawn-color-4) 36%,
      var(--dawn-color-5) 48%,
      var(--dawn-color-6) 60%,
      var(--dawn-color-7) 76%,
      var(--dawn-color-8) 100%
    );
  }

  .dark .dawn-gradient::before {
    /* Already inherits from parent's CSS variables */
  }
}

