export default function PillarsSection({
  title = "One Unified Platform",
  subtitle = "A self-hosted stack to orchestrate digital asset operations on any chain.\nMonitored 24×7 by global experts with AI-automated compliance.",
  customFounderQuotes,
  customOrder
}: PillarsSectionProps = {}) {
  // Reorder: Distribution (0) -> Trading (1) -> Payments (2) -> Service (3)
  const defaultOrder = ["distribution", "trading", "payments", "service"];
  const orderedKeys = customOrder || defaultOrder;

  const [currentStep, setCurrentStep] = useState(0);
  const [animatedStep, setAnimatedStep] = useState(0);
  const [isAutoPlaying, setIsAutoPlaying] = useState(true);
  const [isWrappingAround, setIsWrappingAround] = useState(false);
  const [isNavScrolled, setIsNavScrolled] = useState(false);
  const prevStepRef = useRef(0);
  const tabsContainerRef = useRef<HTMLDivElement>(null);
  const { openModal, CalendlyModal } = useCalendlyModal();

  // Detect scroll for glass effect and disable auto-play on scroll only if currently auto-playing
  useEffect(() => {
    const handleScroll = () => {
      const pillarNav = document.getElementById('pillar-navigation');
      if (pillarNav) {
        const navTop = pillarNav.getBoundingClientRect().top;
        setIsNavScrolled(navTop <= 80);
      }
      // Only reset if auto-play is currently active (user hasn't interacted yet)
      if (isAutoPlaying) {
        setIsAutoPlaying(false);
        setCurrentStep(0);
        setAnimatedStep(0);
      }
    };
    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, [isAutoPlaying]);

  // Auto-scroll mobile tabs container to center active tab (only on manual click, not auto-play)
  useEffect(() => {
    if (tabsContainerRef.current && window.innerWidth < 640 && !isAutoPlaying) {
      const container = tabsContainerRef.current;
      const activeTab = container.querySelector(`[data-tab-index="${currentStep}"]`);
      if (activeTab) {
        activeTab.scrollIntoView({ behavior: 'smooth', block: 'nearest', inline: 'center' });
      }
    }
  }, [currentStep, isAutoPlaying]);

  // Detect wrap-around (when cycling from last step back to first)
  useEffect(() => {
    if (currentStep < prevStepRef.current) {
      setIsWrappingAround(true);
      setTimeout(() => setIsWrappingAround(false), 50);
    }
    prevStepRef.current = currentStep;
  }, [currentStep]);

  // Auto-advance nav animation every 3 seconds (without switching actual tabs)
  useEffect(() => {
    if (!isAutoPlaying) return;
    const timer = setInterval(() => {
      setAnimatedStep(prev => (prev + 1) % orderedKeys.length);
    }, 3000);
    return () => clearInterval(timer);
  }, [isAutoPlaying, orderedKeys.length]);

  const handleStepClick = (index: number) => {
    setCurrentStep(index);
    setIsAutoPlaying(false);
    // Scroll to pillar content
    setTimeout(() => {
      document.getElementById('pillar-content')?.scrollIntoView({ behavior: 'smooth', block: 'start' });
    }, 50);
  };

  // Listen for pillar activation events from hero CTAs
  useEffect(() => {
    const handleActivatePillar = (event: CustomEvent) => {
      const pillarIndex = orderedKeys.indexOf(event.detail);
      if (pillarIndex !== -1) {
        setCurrentStep(pillarIndex);
        setIsAutoPlaying(false);
        // Scroll to pillar content
        setTimeout(() => {
          document.getElementById('pillar-content')?.scrollIntoView({ behavior: 'smooth', block: 'start' });
        }, 50);
      }
    };

    window.addEventListener('activatePillar', handleActivatePillar as EventListener);
    return () => {
      window.removeEventListener('activatePillar', handleActivatePillar as EventListener);
    };
  }, [orderedKeys]);

  // REWRITTEN QUOTES & UPDATED DATA: Quotes are now more specific, empathetic, and powerful.
  // Founder images are now included.
  // Reordered: Distribution (step 1) -> Trading (step 2) -> Payments (step 3) -> Service (step 4/spiral)
  const defaultPillars = {
    distribution: {
      label: "Tokenize Deposits", color: "albor-plum", title: "Bank-Issued Stablecoin", icon: Workflow,
      description: "Launch your own stablecoin, backed 1:1 by deposits held exclusively in your bank's omnibus accounts.",
      visual: <MintingVisual />,
      founderQuote: {
        quote: "Building trust in a regulated stablecoin begins with two non-negotiable mandates: real-time settlement of deposits and withdrawals, and audit-ready asset backing.",
        name: "David Cass",
        title: "Chairman | Former Federal Reserve Regulator",
        image: davidImage
      },
      features: [{ icon: Workflow, title: "Automated Issuance", description: "Client deposits into your omnibus account trigger 1:1 issuance of your stablecoin to their wallet." }, { icon: CreditCard, title: "Instant Redemption", description: "Clients redeem your stablecoin for immediate withdrawal from your omnibus account." }, { icon: CheckCircle, title: "Audit-Ready Reconciliation", description: "On-demand, audit-ready reports reconciling every mint and redemption with omnibus deposits." },],
      cta: "Explore APIs"
    },
    trading: {
      label: "Attract Liquidity", color: "albor-blue", title: "Automated FX Provider", icon: Coins,
      description: "Secure 24/7 Digital Dollar liquidity for your Bank Stablecoin, funded by a network of Market Makers.",
      visual: <ExecutiveLiquidityFlow />,
      founderQuote: {
        quote: "To capture global flows, stablecoins must offer deep liquidity. This requires a market structure that attracts institutional capital while adhering to strict counterparty compliance.",
        name: "David Cass",
        title: "Chairman | Former CISO at GSR",
        image: davidImage
      },
      features: [{ icon: Store, title: "Third-Party Liquidity Pools", description: "Enable Market Makers to fund secure liquidity pools with Digital Dollars and your Bank Stablecoins." }, { icon: TrendingUp, title: "BIS-Aligned Pricing Engine", description: "Offer optimal exchange rates using pricing models validated by the Bank for International Settlements." }, { icon: DollarSign, title: "Automated Yield Distribution", description: "Capture FX fees on every swap and automatically distribute yield to attract and retain Market Maker liquidity." },],
      cta: "Explore DEX"
    },
    payments: {
      label: "Transact Globally", color: "albor-teal", title: "One-Tap Checkout", icon: Globe,
      description: "Enable clients to send and receive Digital Dollars worldwide directly using your Bank Stablecoin.",
      visual: <PaymentsVisual />,
      founderQuote: {
        quote: "Stablecoins drive volume when any user, anywhere can trade them with their existing funds — be it fiat in banks or crypto in wallets.",
        name: "Nilesh Khaitan",
        title: "CEO | Ex-Venmo/PayPal Crypto",
        image: nileshImage
      },
      features: [{ icon: Route, title: "Smart Global Routing", description: "Find the best settlement route across diverse liquidity pools to transact with external wallets and international banks." }, { icon: KeyIcon, title: "Peer-to-Peer Settlement", description: "Settle routes peer-to-peer between the bank, liquidity pools, and counterparty wallets. Albor never holds funds." }, { icon: Zap, title: "Interchange Revenue", description: "Automatically capture fees on Digital Dollar payments that currently bypass your bank entirely." },],
      cta: "Explore Router"
