      title: "Sovereign Infrastructure",
      description: "Satisfy regulatory and risk committee mandates by holding full control of your keys and your data.",
      visual: <ExecutiveServiceFlow />,
      founderQuote: {
        quote: "No regulated financial institution should cede their monetary or data sovereignty. You either control your infrastructure or you don't control your business.",
        name: "Nilesh Khaitan",
        title: "CEO | Ex-Venmo/PayPal Crypto",
        image: nileshImage
      },
      features: [{ icon: KeyIcon, title: "Connect Your Key Providers", description: "Integrate with your existing wallet providers and key governance systems." }, { icon: ShieldCheck, title: "Enforce AML/KYC Checks", description: "Leverage Compliance integrations for real-time AML/KYC checks for all transactions." }, { icon: Shield, title: "Secure Data Sovereignty", description: "Deploy our stack in your own single-tenant private cloud or a local data-resident zone." }, ],
      cta: "Explore Compliance"
    },
  };

  // Override founder quotes if custom ones provided
  const pillars = customFounderQuotes ? {
    ...defaultPillars,
    ...Object.fromEntries(
      customFounderQuotes.map(customQuote => [
        customQuote.pillarKey,
        {
          ...defaultPillars[customQuote.pillarKey as keyof typeof defaultPillars],
          founderQuote: {
            quote: customQuote.quote,
            name: customQuote.name,
            title: customQuote.title,
            image: customQuote.image
          }
        }
      ])
