@import url('https://fonts.googleapis.com/css2?family=General+Sans:ital,wght@0,200;0,300;0,400;0,500;0,600;0,700;0,800;1,200;1,300;1,400;1,500;1,600;1,700;1,800&display=swap');
@tailwind base;
@tailwind components;
@tailwind utilities;

:root {
  --background: hsl(0, 0%, 0%);
  --foreground: hsl(200, 6.6667%, 91.1765%);
  --card: hsl(228, 9.8039%, 10%);
  --card-foreground: hsl(0, 0%, 85.0980%);
  --popover: hsl(0, 0%, 0%);
  --popover-foreground: hsl(200, 6.6667%, 91.1765%);
  --primary: hsl(203.7736, 87.6033%, 52.5490%);
  --primary-foreground: hsl(0, 0%, 100%);
  --secondary: hsl(195.0000, 15.3846%, 94.9020%);
  --secondary-foreground: hsl(210, 25%, 7.8431%);
  --muted: hsl(0, 0%, 9.4118%);
  --muted-foreground: hsl(210, 3.3898%, 46.2745%);
  --accent: hsl(205.7143, 70%, 7.8431%);
  --accent-foreground: hsl(203.7736, 87.6033%, 52.5490%);
  --destructive: hsl(356.3033, 90.5579%, 54.3137%);
  --destructive-foreground: hsl(0, 0%, 100%);
  --border: hsl(210, 5.2632%, 14.9020%);
  --input: hsl(207.6923, 27.6596%, 18.4314%);
  --ring: hsl(202.8169, 89.1213%, 53.1373%);
  --radius: 1.3rem;

  /* Albor Brand Colors - Official Final Palette */
  --albor-foundation: hsl(223, 41%, 20%); /* #1E2A48 - Background */
  --albor-dawn: hsl(37.9, 86.8%, 64.7%); /* #F1BD76 - Primary Accent */
  --albor-white: hsl(0, 0%, 100%); /* #FFFFFF - Text/Data */
  --albor-teal: hsl(176.4, 72.2%, 64.3%); /* #5DD4E0 - Growth/Success */
  --albor-blue: hsl(215.0, 73%, 47%); /* #2169D1 - Trust/Links */
  --albor-innovation: hsl(269.4, 100%, 68.2%); /* #A885FF - Decorative */
  --albor-logo-blue: hsl(215.0, 73%, 47%); /* #2169D1 - Decorative */

  /* Deprecated - kept for backwards compatibility */
  --albor-gold: hsl(37.9, 86.8%, 64.7%); /* #F1BD76 - Now using Dawn */
  --albor-plum: hsl(268.6, 34.8%, 45.5%); /* #5C4C8C - Now using Innovation */

  /* Albor Gray Scale */
  --albor-gray-50: hsl(210, 20%, 98%);
  --albor-gray-100: hsl(220, 14%, 93%);
  --albor-gray-200: hsl(220, 13%, 91%);
  --albor-gray-400: hsl(218, 11%, 65%);
  --albor-gray-500: hsl(220, 9%, 46%);
  --albor-gray-600: hsl(215, 14%, 34%);
  --albor-gray-900: hsl(220, 39%, 11%);

  /* Font Family */
  --font-sans: 'General Sans', system-ui, -apple-system, sans-serif;
}

@layer base {
  * {
    @apply border-border;
  }

  body {
    @apply font-sans antialiased bg-background text-foreground;
    font-family: var(--font-sans);
  }

  html {
    scroll-behavior: smooth;
  }
}

@layer utilities {
  .animate-fade-in {
    animation: fadeIn 0.6s ease-out;
  }

  .animate-slide-up {
    animation: slideUp 0.6s ease-out;
  }

  .albor-gradient {
    background: linear-gradient(135deg, var(--albor-gradient-start), var(--albor-gradient-middle), var(--albor-gradient-end));
  }

  .albor-gradient-text {
    background: linear-gradient(135deg, var(--albor-gradient-start), var(--albor-gradient-middle), var(--albor-gradient-end));
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    background-clip: text;
  }

  .glass-card {
    background: rgba(255, 255, 255, 0.1);
    backdrop-filter: blur(10px);
    border: 1px solid rgba(255, 255, 255, 0.2);
  }

  @keyframes fadeIn {
    from {
      opacity: 0;
    }
    to {
      opacity: 1;
    }
  }

  @keyframes slideUp {
    from {
      opacity: 0;
      transform: translateY(20px);
    }
    to {
      opacity: 1;
      transform: translateY(0);
    }
  }

  @keyframes depthShift {
    0%, 100% {
      box-shadow: 0 10px 30px rgba(0, 0, 0, 0.1);
      transform: translateY(0);
    }
    50% {
      box-shadow: 0 15px 40px rgba(0, 0, 0, 0.15);
      transform: translateY(-2px);
    }
  }

  .animate-gradient {
    animation: depthShift 5s ease-in-out infinite;
  }

  @keyframes navGlow {
    0%, 100% {
      box-shadow: 0 4px 24px rgba(0, 0, 0, 0.12), inset 0 1px 0 rgba(255, 255, 255, 0.15);
    }
    50% {
      box-shadow: 0 6px 32px rgba(0, 0, 0, 0.18), inset 0 1px 0 rgba(255, 255, 255, 0.2);
    }
  }

  .animate-nav-glow {
    animation: navGlow 6s ease-in-out infinite;
  }

  /* Dawn Gradient - dynamic radial that changes with scroll */
  .dawn-gradient {
    position: relative;
    --dawn-color-1: #070911;
    --dawn-color-2: #070911;
    --dawn-color-3: #0A0D15;
    --dawn-color-4: #0F1218;
    --dawn-color-5: #15151C;
    --dawn-color-6: #1A1818;
    --dawn-color-7: #261E14;
    --dawn-color-8: #3A280E;
  }

  .dawn-gradient::before {
    content: '';
    position: fixed;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    z-index: -1;
    pointer-events: none;
    /* Radial gradient: dark dominant color at center, spreading outward subtly */
    background: radial-gradient(
      ellipse 120% 120% at 50% 50%,
      var(--dawn-color-1) 0%,
      var(--dawn-color-2) 12%,
      var(--dawn-color-3) 24%,
      var(--dawn-color-4) 36%,
      var(--dawn-color-5) 48%,
      var(--dawn-color-6) 60%,
      var(--dawn-color-7) 76%,
      var(--dawn-color-8) 100%
    );
  }

  .dark .dawn-gradient::before {
    /* Already inherits from parent's CSS variables */
  }
}

